import { useState } from "react";
import axiosInstance from "../utils/axios";
import {
  MDBTabsContent,
  MDBBtn,
  MDBInput,
  MDBValidation,
  MDBValidationItem,
  MDBRow,
  MDBCol
} from 'mdb-react-ui-kit';
import Header from '../components/header';

function AddDebts() {

  type PaymentPlan = {
    paymentDate: string;
    paymentAmount: number;
  };

  type FormData = {
    debtName: string;
    lenderName: string;
    debtAmount: number | null;
    interestRate: number | null;
    amount: number | null;
    paymentStart: string;
    installment: number | null;
    description: string;
    paymentPlan: PaymentPlan[];
  };

  const [error, setError] = useState('');
  const [formData, setFormData] = useState<FormData>({
    debtName: '',
    lenderName: '',
    debtAmount: null,
    interestRate: null,
    amount: null,
    paymentStart: new Date().toISOString().split('T')[0],
    installment: null,
    description: '',
    paymentPlan: [
      {
        paymentDate: new Date().toISOString().split('T')[0],
        paymentAmount: 0,
      }
    ]
  });

  const formatCurrency = (value: number | null) => {
    if (value === null) return '';
    return new Intl.NumberFormat('tr-TR', { style: 'currency', currency: 'TRY' }).format(value);
  };

  const parseCurrency = (value: string) => {
    const cleanedValue = value.replace(/[^0-9,-]+/g, '').replace(',', '.');
    return parseFloat(cleanedValue);
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    let parsedValue: any = value;

    if (name === 'debtAmount' | name === 'amount' || name === 'installment') {
      parsedValue = parseCurrency(value);
    }

    setFormData(prevState => ({
      ...prevState,
      [name]: name === 'paymentStart' ? value : parsedValue
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    const dataToSend = {
      ...formData,
      debtAmount: formData.debtAmount ?? 0,
      interestRate: formData.interestRate ?? 0,
      amount: formData.amount ?? 0,
      installment: formData.installment ?? 0
    };

    try {
      const response = await axiosInstance.post('/finance/debt', dataToSend);
      if (response.data.status === 'success') {
        // Handle success
      } else if (response.data.status === 'error') {
        // Handle error
      }
    } catch (error) {
      setError('An error occurred while submitting the form.');
    }
  };

  return (
    <div className="bg-light pages">
      <Header />
      <div>Add Debt</div>
      {error && <div className='error-message'>{error}</div>}
      <div className="containerForm">
        <MDBTabsContent>
          <MDBValidation onSubmit={handleSubmit}>
            <MDBRow className='mb-2'>
              <MDBCol>
                <MDBValidationItem feedback='Please enter Debt Name' invalid className='form-item'>
                  <span>Debt Name</span>
                  <MDBInput wrapperClass='mb-4' required name='debtName' type='text' value={formData.debtName} onChange={handleChange} />
                </MDBValidationItem>
              </MDBCol>
              <MDBCol>
                <MDBValidationItem feedback='Please enter Lender Name' invalid className='form-item'>
                  <span>Lender Name</span>
                  <MDBInput wrapperClass='mb-4' required name='lenderName' type='text' value={formData.lenderName} onChange={handleChange} />
                </MDBValidationItem>
              </MDBCol>
            </MDBRow>

            <MDBRow className='mb-2'>
              <MDBCol>
                <MDBValidationItem feedback='Please enter Debt Amount' invalid className='form-item'>
                  <span>Debt Amount</span>
                  <MDBInput wrapperClass='mb-4' required name='debtAmount' type='text' value={formatCurrency(formData.debtAmount)} onChange={handleChange} />
                </MDBValidationItem>
              </MDBCol>
              <MDBCol>
                <MDBValidationItem feedback='Please enter Interest Rate' invalid className='form-item'>
                  <span>Interest Rate</span>
                  <MDBInput wrapperClass='mb-4' required name='interestRate' type='text' value={formatCurrency(formData.interestRate)} onChange={handleChange} />
                </MDBValidationItem>
              </MDBCol>
            </MDBRow>

            <MDBRow className='mb-2'>
              <MDBCol>
                <MDBValidationItem feedback='Please enter Amount' invalid className='form-item'>
                  <span>Amount</span>
                  <MDBInput wrapperClass='mb-4' required name='amount' type='text' value={formatCurrency(formData.amount)} onChange={handleChange} />
                </MDBValidationItem>
              </MDBCol>
              <MDBCol>
                <MDBValidationItem feedback='Please enter Payment Start Date' invalid className='form-item'>
                  <span>Payment Start Date</span>
                  <MDBInput
                    wrapperClass='mb-4'
                    required
                    name='paymentStart'
                    type='date'
                    value={formData.paymentStart}
                    onChange={handleChange}
                  />
                </MDBValidationItem>
              </MDBCol>
            </MDBRow>

            <MDBRow className='mb-2'>
              <MDBCol>
                <MDBValidationItem feedback='Please enter Installment' invalid className='form-item'>
                  <span>Installment</span>
                  <MDBInput wrapperClass='mb-4' required name='installment' type='text' value={formatCurrency(formData.installment)} onChange={handleChange} />
                </MDBValidationItem>
              </MDBCol>
              <MDBCol>
                <MDBValidationItem feedback='Please enter Description' invalid className='form-item'>
                  <span>Description</span>
                  <MDBInput wrapperClass='mb-4' required name='description' type='text' value={formData.description} onChange={handleChange} />
                </MDBValidationItem>
              </MDBCol>
            </MDBRow>

            <MDBBtn type="submit" className="mb-4 w-100">Create Payment Plan</MDBBtn>
          </MDBValidation>
        </MDBTabsContent>
      </div>
    </div>
  );
}

export default AddDebts;
