// debtSlice.ts

import { createSlice, PayloadAction } from '@reduxjs/toolkit';

interface PaymentPlan {
  paymentDate: string;
  paymentAmount: number;
}

interface FormData {
  debtName: string;
  lenderName: string;
  debtAmount: number;
  interestRate: number;
  amount: number;
  paymentStart: string;
  installment: number;
  description: string;
  paymentPlan: PaymentPlan[];
}

interface FormState {
  formData: FormData;
}

const initialState: FormState = {
  formData: {
    debtName: '',
    lenderName: '',
    debtAmount: 0,
    interestRate: 0,
    amount: 0,
    paymentStart: '',
    installment: 0,
    description: '',
    paymentPlan: [],
  },
};

const debtSlice = createSlice({
  name: 'debt',
  initialState,
  reducers: {
    setFormData(state, action: PayloadAction<FormData>) {
      state.formData = action.payload;
    },
    updateFormDataFocus(state, action: PayloadAction<{ name: keyof FormData; value: string }>) {
      const { name, value } = action.payload;
      switch (name) {
        case 'debtName':
          state.formData.debtName = value;
          break;
        case 'lenderName':
          state.formData.lenderName = value;
          break;
        case 'debtAmount':
          state.formData.debtAmount = parseFloat(value);
          break;
        // Diğer alanlar için aynı şekilde devam edebilirsiniz
      }
    },
    updateFormDataBlur(state, action: PayloadAction<{ name: keyof FormData; value: string }>) {
      const { name, value } = action.payload;
      switch (name) {
        case 'debtName':
          state.formData.debtName = value;
          break;
        case 'lenderName':
          state.formData.lenderName = value;
          break;
        case 'debtAmount':
          state.formData.debtAmount = parseFloat(value);
          break;
        // Diğer alanlar için aynı şekilde devam edebilirsiniz
      }
    },
  },
});

export const { setFormData, updateFormDataFocus, updateFormDataBlur } = debtSlice.actions;

export default debtSlice.reducer;
