import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
import axiosInstance from "../utils/axios";
import { MDBListGroup, MDBListGroupItem, MDBCheckbox } from 'mdb-react-ui-kit';
import Header from '../components/header';



function PaymentPlanPage() {
  const { id } = useParams();
  const [error, setError] = useState<string>('');
  const [loading, setLoading] = useState<boolean>(true);
  const [paymentPlanData, setPaymentPlanData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axiosInstance.get(`/finance/payment-plans/${id}`);
        if (response.data.status === 'success') {
          setPaymentPlanData(response.data.data);
        } else {
          setError('Veriler alınırken bir hata oluştu.');
        }
      } catch (error) {
        setError('Veriler alınırken bir hata oluştu.');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [id]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div className='error-message'>{error}</div>;
  }

  return (
    <div className="bg-light pages">
      <Header />
      <div>Payment Plan</div>
      {/* {paymentPlanData && (
        <div>
          <p>Payment Amount: {paymentPlanData.paymentAmount}</p>
          <p>Payment Date: {new Date(paymentPlanData.paymentDate).toLocaleDateString()}</p>
          {/* Render other payment plan details here 
        </div>
      )} */}
      {paymentPlanData && (
        <MDBListGroup style={{ minWidth: '22rem' }} light>
      <MDBListGroupItem tag='label'>
        <MDBCheckbox label='Cras justo odio' />
      </MDBListGroupItem>

      <MDBListGroupItem tag='label'>
        <MDBCheckbox label='Dapibus ac facilisis in' />
      </MDBListGroupItem>

      <MDBListGroupItem tag='label'>
        <MDBCheckbox label='Morbi leo risus' />
      </MDBListGroupItem>

      <MDBListGroupItem tag='label'>
        <MDBCheckbox label='Porta ac consectetur ac' />
      </MDBListGroupItem>

      <MDBListGroupItem tag='label'>
        <MDBCheckbox label='Vestibulum at eros' />
      </MDBListGroupItem>
    </MDBListGroup>
      )}

    </div>
  );
}

export default PaymentPlanPage;
