import React, { useState } from "react";
import axiosInstance from "../utils/axios";
import { useNavigate } from "react-router-dom";
import Header from '../components/header';
import DebtForm from '../components/DebtForm';

interface PaymentPlan {
  paymentDate: string;
  paymentAmount: number;
};

interface FormData {
  debtName: string;
  lenderName: string;
  debtAmount: number;
  interestRate: number;
  amount: number;
  paymentStart: string;
  installment: number;
  description: string;
  paymentPlan: PaymentPlan[];
};

function AddDebts() {
  const navigate = useNavigate();
  const [error, setError] = useState<string>('');

  const handleSubmit = async (formData: FormData) => {
    const dataToSend = {
      ...formData,
      debtAmount: formData.debtAmount,
      interestRate: formData.interestRate ?? 0,
      amount: formData.amount,
      installment: formData.installment ?? 0
    };


    try {
      const response = await axiosInstance.post('/finance/debt', dataToSend);
      if (response.data.status === 'success') {
        const id = response.data.data.id;
        navigate(`/payment-plan/${id}`);
      } else if (response.data.status === 'error') {
        setError(response.data.data);
      }
    } catch (error) {
      setError('Formu gönderirken bir hata oluştu.');
    }
  };

  return (
    <div className="bg-light pages">
      <Header />
      <div>Borç Ekle</div>
      <div className="containerForm">
        <DebtForm onSubmit={handleSubmit} error={error} />
      </div>
    </div>
  );
}

export default AddDebts;
