import {
    MDBTabsContent,
    MDBBtn,
    MDBInput,
    MDBValidation,
    MDBValidationItem,
}
    from 'mdb-react-ui-kit';
import axios from 'axios';
import { useState } from 'react';

function Register() {

    interface FormData {
        name: string;
        email: string;
        password: string;
        passwordAgain: string;
    }

    const [formData, setFormData] = useState<FormData>({
        name: '',
        email: '',
        password: '',
        passwordAgain: '',
    })
    const [error, setError] = useState('')



    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };



    const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
        e. preventDefault()
        if (!(formData.password === formData.passwordAgain)) {
            setError('passwords do not match');
            return ;
        }
        setError('');
        setPasswordError(false)
            try {
                const response = await axios.post('https://study.logiper.com/auth/register', formData);

                console.log('Response from server:', response.data);
            } catch (error) {
                console.error('Error:', error);
            }


console.log(formData)
    };

    return (

        <>
            {error && <div>{error}</div>}
                <MDBTabsContent >
                    <MDBValidation  onSubmit={handleSubmit}>
                        <MDBValidationItem  feedback='Please enter name surname' invalid>
                            <MDBInput wrapperClass='mb-4' required  label='Name Surname' name='name' type='text' value={formData.name} onChange={handleChange} />
                        </MDBValidationItem>
                        <MDBValidationItem feedback='Please enter email' invalid>
                            <MDBInput wrapperClass='mb-4' required  label='Email' name='email' type='email' value={formData.email} onChange={handleChange} />
                        </MDBValidationItem>
                        <MDBValidationItem feedback='Please enter  password' invalid>
                            <MDBInput wrapperClass='mb-4'    required  label='Password' name='password' type='password' value={formData.password} onChange={handleChange} />
                        </MDBValidationItem >
                        <MDBValidationItem feedback='Please re-enter the password' invalid>
                            <MDBInput wrapperClass='mb-4'   required  label='Password Again' name='passwordAgain' value={formData.passwordAgain}onChange={handleChange} type='password' />
                        </MDBValidationItem>
                        <MDBBtn type="submit"   className="mb-4 w-100">Sign up</MDBBtn>
                    </MDBValidation>
                </MDBTabsContent>
        </>

    );
}

export default Register;
