import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
import axiosInstance from "../utils/axios";
import {
    MDBTabsContent,
    MDBRow,
    MDBCol,
    MDBListGroup,
    MDBListGroupItem,
    MDBCheckbox

} from 'mdb-react-ui-kit';
import Header from '../components/header';

interface PaymentPlanData {
    id: string;
    paymentAmount: number;
    paymentDate: string;
    isPaid: boolean;
}

function PaymentPlanPage() {
    const { id } = useParams<{ id: string }>();
    const [error, setError] = useState<string>('');
    const [paymentPlans, setPaymentPlans] = useState<PaymentPlanData[]>([]);




    const fetchPaymentPlans = async () => {
        try {
            const response = await axiosInstance.get<{ status: string; data: PaymentPlanData[] }>(`/finance/payment-plans/${id}`);
            if (response.data.status === 'success') {
                setPaymentPlans(response.data.data);
            } else {
                setError('Veriler alınırken bir hata oluştu.');
            }
        } catch (error) {
            setError('Veriler alınırken bir hata oluştu.');
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchPaymentPlans();
    }, [id]);


    const handleUpdate = async (plan: PaymentPlanData) => {
        const updatedData = {
            "paymentDate": plan.paymentDate,
            "paymentAmount": plan.paymentAmount,
            "isPaid": !(plan.isPaid)
        };

        try {
            await axiosInstance.put(`/finance/payment-plans/${plan.id}`, updatedData);
            fetchPaymentPlans();
        } catch (error) {
            setError('Borç silinirken bir hata oluştu.');
        }
    };


    return (
        <div className="bg-light pages">
            <Header />
            <div>Payment Plan</div>               
                <MDBTabsContent className='p-5'>
                    {paymentPlans.map((plan: PaymentPlanData, index) => (
                        <div key={plan.id} className='border'>
                            <MDBListGroup style={{ minWidth: '22rem' }} light >
                                <MDBListGroupItem tag='label'>
                                <MDBRow >
                                <MDBCol className="d-flex justify-content-center align-items-center" >
                                    <MDBCheckbox label={plan.isPaid == true ? 'Paid' : 'Not Paid'} checked={plan.isPaid} onClick={() => { handleUpdate(plan) }}/>
                                </MDBCol>
                                    <MDBCol>
                                        <p className='text-bg-warning p-1 text-light'>{index + 1}st Installment</p>
                                        <p> <span className="fw-bold">Payment Amount:</span> {plan.paymentAmount} </p>
                                        <p> <span className="fw-bold"> Payment Date:</span>  {new Date(plan.paymentDate).toISOString().split('T')[0]}</p>
                                        </MDBCol>
                                        </MDBRow>
                                </MDBListGroupItem>
                            </MDBListGroup>

                        </div>
                    ))}
                </MDBTabsContent>
        </div>
    );
}

export default PaymentPlanPage;
