import React, { useState, useEffect } from 'react';
import axiosInstance from '../utils/axios';
import { MDBInput, MDBBtn, MDBValidation, MDBValidationItem, MDBRow, MDBCol } from 'mdb-react-ui-kit';

const PaymentForm = ({ id, onSubmit }) => {
  const [formData, setFormData] = useState({
    debtName: '',
    lenderName: '',
    debtAmount: 0,
    interestRate: 0,
    paymentStart: new Date().toISOString().split('T')[0],
    installment: 0,
    description: '',
    paymentPlan: [
      {
        paymentDate: new Date().toISOString().split('T')[0],
        paymentAmount: 0,
      }
    ]
  });

  useEffect(() => {
    if (id) {
      fetchData();
    }
  }, [id]);

  const fetchData = async () => {
    try {
      const response = await axiosInstance.get(`/finance/debt/${id}`);
      if (response.data.status === 'success') {
        setFormData(response.data.data);
      } else if (response.data.status === 'error') {
        console.error(response.data.message);
      }
    } catch (error) {
      console.error('Verileri alırken bir hata oluştu.');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await onSubmit(formData);
  };

  const handleFocus = (e) => {
    if (parseInt(e.target.value) === 0) {
      setFormData(prevState => ({
        ...prevState,
        [e.target.name]: ''
      }));
    }
  };

  const handleBlur = (e) => {
    if (!e.target.value) {
      setFormData(prevState => ({
        ...prevState,
        [e.target.name]: 0
      }));
    }
  };

  return (
    <MDBValidation onSubmit={handleSubmit}>
      <MDBRow className='mb-2'>
        <MDBCol>
          <MDBValidationItem feedback='Please enter debt name' invalid className='form-item'>
            <span>Debt Name</span>
            <MDBInput wrapperClass='mb-4' required name='debtName' type='text' value={formData.debtName} onChange={handleChange} />
          </MDBValidationItem>
        </MDBCol>
        <MDBCol>
          <MDBValidationItem feedback='Please enter lender' invalid className='form-item'>
            <span>Lender</span>
            <MDBInput wrapperClass='mb-4' required name='lenderName' type='text' value={formData.lenderName} onChange={handleChange} />
          </MDBValidationItem>
        </MDBCol>
      </MDBRow>

      <MDBRow className='mb-2'>
        <MDBCol>
          <MDBValidationItem feedback='Please enter amount' invalid className='form-item'>
            <span>Amount</span>
            <MDBInput
              wrapperClass='mb-4'
              required
              name='debtAmount'
              type='number'
              value={formData.debtAmount}
              onChange={handleChange}
              onFocus={handleFocus}
              onBlur={handleBlur}
            />
          </MDBValidationItem>
        </MDBCol>
        <MDBCol>
          <MDBValidationItem feedback='Please enter interest rate' invalid className='form-item'>
            <span>Interest rate (%)</span>
            <MDBInput
              wrapperClass='mb-4'
              required
              name='interestRate'
              type='number'
              value={formData.interestRate}
              onChange={handleChange}
              onFocus={handleFocus}
              onBlur={handleBlur}
            />
          </MDBValidationItem>
        </MDBCol>
      </MDBRow>

      <MDBRow className='mb-2'>
        <MDBCol>
          <MDBValidationItem feedback='Please enter starting date' invalid className='form-item'>
            <span>Starting Date</span>
            <MDBInput
              wrapperClass='mb-4'
              required
              name='paymentStart'
              type='date'
              value={formData.paymentStart}
              onChange={handleChange}
            />
          </MDBValidationItem>
        </MDBCol>
        <MDBCol>
          <MDBValidationItem feedback='Please enter number of installments' invalid className='form-item'>
            <span>Number of Installments</span>
            <MDBInput
              wrapperClass='mb-4'
              required
              name='installment'
              type='number'
              value={formData.installment}
              onChange={handleChange}
              onFocus={handleFocus}
              onBlur={handleBlur}
            />
          </MDBValidationItem>
        </MDBCol>
      </MDBRow>

      <MDBRow className='mb-2'>
        <MDBCol>
          <MDBValidationItem feedback='Please enter description' invalid className='form-item'>
            <span>Description</span>
            <MDBInput wrapperClass='mb-4' required name='description' type='text' value={formData.description} onChange={handleChange} />
          </MDBValidationItem>
        </MDBCol>
      </MDBRow>

      <MDBBtn type="submit" className="mb-4 w-100">Submit</MDBBtn>
    </MDBValidation>
  );
};

export default PaymentForm;
