import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
import axiosInstance from "../utils/axios";
import {
  MDBTabsContent,
  MDBBtn,
  MDBInput,
  MDBValidation,
  MDBValidationItem,
  MDBRow,
  MDBCol
} from 'mdb-react-ui-kit';
import Header from '../components/header';



function PaymentPlanPage() {
  const { id } = useParams<{ id: string }>();
  const [error, setError] = useState<string>('');
  const [loading, setLoading] = useState<boolean>(true);
  const [paymentPlanData, setPaymentPlanData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axiosInstance.get>(`/finance/payment-plan/${id}`);
        if (response.data.status === 'success') {
          setPaymentPlanData(response.data.data);
        } else {
          setError('Veriler alınırken bir hata oluştu.');
        }
      } catch (error) {
        setError('Veriler alınırken bir hata oluştu.');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [id]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div className='error-message'>{error}</div>;
  }

  return (
    <div className="bg-light pages">
      <Header />
      <div>Payment Plan</div>
      {/* {paymentPlanData && (
        <div>
          <p>Payment Amount: {paymentPlanData.paymentAmount}</p>
          <p>Payment Date: {new Date(paymentPlanData.paymentDate).toLocaleDateString()}</p>
          {/* Render other payment plan details here 
        </div>
      )} */}
    </div>
  );
}

export default PaymentPlanPage;
