import axiosInstance from "../utils/axios";
import { useEffect, useState } from "react";
import {
  MDBTabsContent,
  MDBBtn,
  MDBInput,
  MDBValidation,
  MDBValidationItem,
  MDBRow,
  MDBCol
} from 'mdb-react-ui-kit';
import Header from '../components/header';
import { useNavigate } from "react-router-dom";
import { useParams } from 'react-router-dom';

function AddPaymentPlanPage() {

  const navigate = useNavigate();
  const { id } = useParams<{ id: string }>();

  type PaymentPlan = {
    paymentDate: string;
    paymentAmount: number;
  };

  type FormData = {
    debtName: string;
    lenderName: string;
    debtAmount: number;
    interestRate: number;
    amount: number;
    paymentStart: string;
    installment: number;
    description: string;
    paymentPlan: PaymentPlan[];
  };

  const [error, setError] = useState<string>('');
  const [formData, setFormData] = useState<FormData>({
    debtName: '',
    lenderName: '',
    debtAmount: 0,
    interestRate: 0,
    amount: 0,
    paymentStart: new Date().toISOString().split('T')[0],
    installment: 0,
    description: '',
    paymentPlan: [
      {
        paymentDate: new Date().toISOString().split('T')[0],
        paymentAmount: 0,
      }
    ]
  });


    const fetchData = async () => {
      try {
        const response = await axiosInstance.get(`/finance/debt/${id}`);
        if (response.data.status === 'success') {
          setFormData(response.data.data);
        } else if (response.data.status === 'error') {
          setError(response.data.message);
        }
      } catch (error) {
        setError('Verileri alırken bir hata oluştu.');
      }
    };

    useEffect(() => {
    fetchData();
  }, []);

  const calculateTotalAmount = (debtAmount: number, interestRate: number) => {
    const total = debtAmount + (debtAmount * (interestRate / 100))
    return total;
  };

  const generatePaymentPlan = (amount: number, installment: number, paymentStart: string): PaymentPlan[] => {
    const totalAmount = amount;
    const paymentAmount = totalAmount / installment;
    const paymentPlans: PaymentPlan[] = [];

    const currentDate = new Date(paymentStart);

    for (let i = 0; i < installment; i++) {
      const paymentDate = currentDate.toISOString().split('T')[0];
      paymentPlans.push({
        paymentDate,
        paymentAmount
      });

      currentDate.setMonth(currentDate.getMonth() + 1);
    }

    return paymentPlans;
  };

  useEffect(() => {
    const paymentPlan = generatePaymentPlan(formData.debtAmount, formData.installment, formData.paymentStart);
    setFormData(prevState => ({
      ...prevState,
      paymentPlan: paymentPlan
    }));
  }, [formData.debtAmount, formData.installment, formData.paymentStart]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;

    setFormData(prevState => {
      const updatedData = {
        ...prevState,
        [name]: name === 'paymentStart' ? value : value
      };


      if (name === 'debtAmount' || name === 'interestRate' || name === 'installment') {
        const numericValue = parseFloat(value);
        if (!isNaN(numericValue)) {
          updatedData[name] = numericValue;
          if (name === 'debtAmount' || name === 'interestRate') {
            updatedData.amount = calculateTotalAmount(updatedData.debtAmount, updatedData.interestRate);
          }
          if (name === 'debtAmount' || name === 'installment') {
            updatedData.paymentPlan = generatePaymentPlan(formData.amount, formData.installment, formData.paymentStart);
          }
        }
      }
      return updatedData;

    });
  };

  const handleFocus = (e: React.FocusEvent<HTMLInputElement>) => parseInt(e.target.value) === 0 && setFormData(prevState => ({ ...prevState, [e.target.name]: '' }));
  const handleBlur = (e: React.FocusEvent<HTMLInputElement>) => !e.target.value && setFormData(prevState => ({ ...prevState, [e.target.name]: 0 }));

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    const dataToSend = {
      ...formData,
      debtAmount: formData.debtAmount,
      interestRate: formData.interestRate ?? 0,
      amount: formData.amount,
      installment: formData.installment ?? 0
    };

    console.log(dataToSend)

    try {
      const response = await axiosInstance.put(`/finance/debt/${id}`, dataToSend);
      if (response.data.status === 'success') {
        const id = response.data.data.id
        fetchData()
      } else if (response.data.status === 'error') {
        setError(response.data.data)
      }
    } catch (error) {
      setError('Formu gönderirken bir hata oluştu.');
    }
  };



  return (
    <div className="bg-light pages">
      <Header />
      <div>Add Payment Plan </div> 
      {error && <div className='error-message'>{error}</div>}
      <div className="containerForm">
        M
        <MDBTabsContent>
          <MDBValidation onSubmit={handleSubmit}>
                <MDBValidationItem feedback='Please enter debt name' invalid className='form-item'>
                  <span>Debt Name</span>
                  <MDBInput wrapperClass='mb-4' required name='debtName' type='text' value={formData.debtName} onChange={handleChange} />
                </MDBValidationItem>

                <MDBValidationItem feedback='Please enter lender' invalid className='form-item'>
                  <span>Lender</span>
                  <MDBInput wrapperClass='mb-4' required name='lenderName' type='text' value={formData.lenderName} onChange={handleChange} />
                </MDBValidationItem>

                <MDBValidationItem feedback='Please enter amount' invalid className='form-item'>
                  <span>Amount</span>
                  <MDBInput
                    wrapperClass='mb-4'
                    required
                    name='debtAmount'
                    type='number'
                    value={formData.debtAmount}
                    onChange={handleChange}
                    onFocus={handleFocus}
                    onBlur={handleBlur}
                  />
                </MDBValidationItem>
                <MDBValidationItem feedback='Please enter interest rate' invalid className='form-item'>
                  <span>Interest rate (%)</span>
                  <MDBInput
                    wrapperClass='mb-4'
                    required
                    name='interestRate'
                    type='number'
                    value={formData.interestRate}
                    onFocus={handleFocus}
                    onBlur={handleBlur}
                    onChange={handleChange}
                  />
                </MDBValidationItem>

                <MDBValidationItem feedback='' invalid className='form-item'>
                  <span>Total</span>
                  <MDBInput
                    wrapperClass='mb-4'
                    required
                    name='amount'
                    type='number'
                    value={formData.amount}

                    disabled
                  />
                </MDBValidationItem>

                <MDBValidationItem feedback='Please enter starting date' invalid className='form-item'>
                  <span>Starting Date</span>
                  <MDBInput
                    wrapperClass='mb-4'
                    required
                    name='paymentStart'
                    type='date'
                    value={formData.paymentStart}
                    onChange={handleChange}
                  />
                </MDBValidationItem>

              <MDBCol>
                <MDBValidationItem feedback='Please enter number of installments' invalid className='form-item'>
                  <span>Number of Installments</span>
                  <MDBInput
                    wrapperClass='mb-4'
                    required
                    name='installment'
                    type='number'
                    value={formData.installment}
                    onChange={handleChange}
                    onFocus={handleFocus}
                    onBlur={handleBlur}
                  />
                </MDBValidationItem>

                <MDBValidationItem feedback='Please enter description' invalid className='form-item'>
                  <span>Description</span>
                  <MDBInput wrapperClass='mb-4' required name='description' type='text' value={formData.description} onChange={handleChange} />
                </MDBValidationItem>
              </MDBCol>


            <MDBBtn type="submit" className="mb-4 w-100">Update Payment Plan</MDBBtn>
          </MDBValidation>
        </MDBTabsContent>
      </div>
    </div>
  );
}

export default AddPaymentPlanPage;
