// PaymentPlanPage.tsx

import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import axiosInstance from "../utils/axios";
import {
    MDBTabsContent,
} from 'mdb-react-ui-kit';
import Header from '../components/header';
import PaymentPlanList from '../components/payment_plan_list';
import { useParams } from 'react-router-dom';
import {setPaymentPlans, } from '../redux/reducers/payment_plan_slice';

interface PaymentPlanData {
    id: string;
    paymentAmount: number;
    paymentDate: string;
    isPaid: boolean;
}

interface RootState {
    paymentPlans: {
        paymentPlans: PaymentPlanData[];
        error: string | null;
    };
}

function PaymentPlanPage() {
    const dispatch = useDispatch();
    const paymentPlans = useSelector((state: RootState) => state.paymentPlans.paymentPlans);
    const { id } = useParams<{ id: string }>();
    const [error, setError] = useState<string>('');


        const fetchPaymentPlans = async () => {
            try {
                const response = await axiosInstance.get(`/finance/payment-plans/${id}`);
                dispatch(setPaymentPlans(response.data.data));
            } catch (error) {
               setError('Veriler alınırken bir hata oluştu.');
            }
        };
        useEffect(() => {

        fetchPaymentPlans();
    }, [dispatch, id]);

    const handleUpdate = async (planId: string, updatedData: Partial<PaymentPlanData>) => {
        try {
            await axiosInstance.put(`/finance/payment-plans/${planId}`, updatedData);
            dispatch(fetchPaymentPlans(id)); 
        } catch (error) {
            console.error('Borç güncellenirken bir hata oluştu.', error);
          setError('Borç güncellenirken bir hata oluştu.');
        }
    };

    return (
        <div className="bg-light pages">
            <Header />
            <div className='px-2 py-4 pagesTitle'>Payment Plan</div>
            {error && <div className='error-message'>{error}</div>}
            <MDBTabsContent className='p-5'>
                {paymentPlans.map((plan) => (
                    <PaymentPlanItem key={plan.id} plan={plan} onUpdate={(updatedData) => handleUpdate(plan.id, updatedData)} />
                ))}
            </MDBTabsContent>
        </div>
    );
}

export default PaymentPlanPage;
