import axiosInstance from "../utils/axios";
import { useEffect, useState } from "react";
import {
  MDBTabsContent,
  MDBBtn,
  MDBInput,
  MDBValidation,
  MDBValidationItem,
  MDBRow,
  MDBCol
} from 'mdb-react-ui-kit';
import Header from '../components/header';
import { useNavigate } from "react-router-dom";

function PaymentPlanPage() {


  useEffect(() => {
    const paymentPlan = generatePaymentPlan(formData.debtAmount, formData.installment, formData.paymentStart);
    setFormData(prevState => ({
      ...prevState,
      paymentPlan: paymentPlan
    }));
  }, [formData.debtAmount, formData.installment, formData.paymentStart]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;

    setFormData(prevState => {
      const updatedData = {
        ...prevState,
        [name]: name === 'paymentStart' ? value : value
      };


      if (name === 'debtAmount' || name === 'interestRate' || name === 'installment') {
        const numericValue = parseFloat(value);
        if (!isNaN(numericValue)) {
          updatedData[name] = numericValue;
          if (name === 'debtAmount' || name === 'interestRate') {
            updatedData.amount = calculateTotalAmount(updatedData.debtAmount, updatedData.interestRate);
          }
          if (name === 'debtAmount' || name === 'installment') {
            updatedData.paymentPlan = generatePaymentPlan(formData.amount, formData.installment, formData.paymentStart);
          }
        }
      }


      return updatedData;

    });
  };

  const handleFocus = (e: React.FocusEvent<HTMLInputElement>,) => {
    const { name, value } = e.target;

    if (parseInt(value) === 0 || parseInt(value) === 0.00) {
      setFormData(prevState => ({
        ...prevState,
        [name]: ''
      }));
    }
  };

  const handleBlur = (e: React.FocusEvent<HTMLInputElement>) => {
    const { name, value } = e.target;

    if (value === '') {
      setFormData(prevState => ({
        ...prevState,
        [name]: 0
      }));
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    const dataToSend = {
      ...formData,
      debtAmount: formData.debtAmount,
      interestRate: formData.interestRate ?? 0,
      amount: formData.amount,
      installment: formData.installment ?? 0
    };

    console.log(dataToSend)

    try {
      const response = await axiosInstance.post('/finance/debt', dataToSend);
      if (response.data.status === 'success') {
        navigate("/payment-plan");
      } else if (response.data.status === 'error') {
        setError(response.data.data)
      }
    } catch (error) {
      setError('Formu gönderirken bir hata oluştu.');
    }
  };



  return (
    <div className="bg-light pages">
      <Header />
      <div>Payment Plan </div>
      {error && <div className='error-message'>{error}</div>}
      <div className="containerForm">
        <MDBTabsContent>
          <MDBValidation onSubmit={handleSubmit}>
            <MDBRow className='mb-2'>
              <MDBCol>
                <MDBValidationItem feedback='Please enter debt name' invalid className='form-item'>
                  <span>Debt Name</span>
                  <MDBInput wrapperClass='mb-4' required name='debtName' type='text' value={formData.debtName} onChange={handleChange} />
                </MDBValidationItem>
              </MDBCol>
              <MDBCol>
                <MDBValidationItem feedback='Please enter lender' invalid className='form-item'>
                  <span>Lender</span>
                  <MDBInput wrapperClass='mb-4' required name='lenderName' type='text' value={formData.lenderName} onChange={handleChange} />
                </MDBValidationItem>
              </MDBCol>
            </MDBRow>

            <MDBRow className='mb-2'>
              <MDBCol>
                <MDBValidationItem feedback='Please enter amount' invalid className='form-item'>
                  <span>Amount</span>
                  <MDBInput
                    wrapperClass='mb-4'
                    required
                    name='debtAmount'
                    type='number'
                    value={formData.debtAmount}
                    onChange={handleChange}
                    onFocus={handleFocus}
                    onBlur={handleBlur}
                  />
                </MDBValidationItem>
              </MDBCol>
              <MDBCol>
                <MDBValidationItem feedback='Please enter interest rate' invalid className='form-item'>
                  <span>Interest rate (%)</span>
                  <MDBInput
                    wrapperClass='mb-4'
                    required
                    name='interestRate'
                    type='number'
                    value={formData.interestRate}
                    onFocus={handleFocus}
                    onBlur={handleBlur}
                    onChange={handleChange}
                  />
                </MDBValidationItem>
              </MDBCol>
            </MDBRow>

            <MDBRow className='mb-2'>
              <MDBCol>
                <MDBValidationItem feedback='' invalid className='form-item'>
                  <span>Total</span>
                  <MDBInput
                    wrapperClass='mb-4'
                    required
                    name='amount'
                    type='number'
                    value={formData.amount}

                    disabled
                  />
                </MDBValidationItem>
              </MDBCol>
              <MDBCol>
                <MDBValidationItem feedback='Please enter starting date' invalid className='form-item'>
                  <span>Starting Date</span>
                  <MDBInput
                    wrapperClass='mb-4'
                    required
                    name='paymentStart'
                    type='date'
                    value={formData.paymentStart}
                    onChange={handleChange}
                  />
                </MDBValidationItem>
              </MDBCol>
            </MDBRow>

            <MDBRow className='mb-2'>
              <MDBCol>
                <MDBValidationItem feedback='Please enter number of installments' invalid className='form-item'>
                  <span>Number of Installments</span>
                  <MDBInput
                    wrapperClass='mb-4'
                    required
                    name='installment'
                    type='number'
                    value={formData.installment}
                    onChange={handleChange}
                    onFocus={handleFocus}
                    onBlur={handleBlur}
                  />
                </MDBValidationItem>
              </MDBCol>
              <MDBCol>
                <MDBValidationItem feedback='Please enter description' invalid className='form-item'>
                  <span>Description</span>
                  <MDBInput wrapperClass='mb-4' required name='description' type='text' value={formData.description} onChange={handleChange} />
                </MDBValidationItem>
              </MDBCol>
            </MDBRow>

            <MDBBtn type="submit" className="mb-4 w-100">Create Payment Plan</MDBBtn>
          </MDBValidation>
        </MDBTabsContent>
      </div>
    </div>
  );
}

export default PaymentPlanPage;
