import React, { useState, useEffect } from 'react';
import { MDBBtn, MDBInput, MDBValidation, MDBValidationItem, MDBRow, MDBCol } from 'mdb-react-ui-kit';
import { useSelector, use } from 'react-redux';
import { RootState } from '../redux/store';

interface PaymentPlan {
  paymentDate: string;
  paymentAmount: number;
};

interface FormData {
  debtName: string;
  lenderName: string;
  debtAmount: number;
  interestRate: number;
  amount: number;
  paymentStart: string;
  installment: number;
  description: string;
  paymentPlan: PaymentPlan[];
};

interface DebtFormProps {
  onSubmit: (data: FormData) => void;
  error: string;
}

const DebtForm: React.FC<DebtFormProps> = ({ onSubmit, error }) => {
  const dispatch = useDispatch();
  const formData = useSelector((state: RootState) => state.form.formData);



  const calculateTotalAmount = (debtAmount: number, interestRate: number) => {
    const total = debtAmount + (debtAmount * (interestRate / 100));
    return total;
  };

  const generatePaymentPlan = (amount: number, installment: number, paymentStart: string): PaymentPlan[] => {
    const totalAmount = amount;
    const paymentAmount = totalAmount / installment;
    const paymentPlans: PaymentPlan[] = [];
    const currentDate = new Date(paymentStart);

    for (let i = 0; i < installment; i++) {
      const paymentDate = currentDate.toISOString().split('T')[0];
      paymentPlans.push({
        paymentDate,
        paymentAmount
      });

      currentDate.setMonth(currentDate.getMonth() + 1);
    }

    return paymentPlans;
  };

  useEffect(() => {
    const paymentPlan = generatePaymentPlan(formData.amount, formData.installment, formData.paymentStart);
    setFormData(prevState => ({
      ...prevState,
      paymentPlan: paymentPlan
    }));
  }, [formData.amount, formData.installment, formData.paymentStart]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;

    setFormData(prevState => {
      const updatedData = {
        ...prevState,
        [name]: name === 'paymentStart' ? value : value
      };

      if (name === 'debtAmount' || name === 'interestRate' || name === 'installment') {
        const numericValue = parseFloat(value);
        if (!isNaN(numericValue)) {
          updatedData[name] = numericValue;
          if (name === 'debtAmount' || name === 'interestRate') {
            updatedData.amount = calculateTotalAmount(updatedData.debtAmount, updatedData.interestRate);
          }
          if (name === 'debtAmount' || name === 'installment') {
            updatedData.paymentPlan = generatePaymentPlan(updatedData.debtAmount, updatedData.installment, updatedData.paymentStart);
          }
        }
      }
      return updatedData;
    });
  };

  const handleFocus = (e: React.FocusEvent<HTMLInputElement>) => parseInt(e.target.value) === 0 && setFormData(prevState => ({ ...prevState, [e.target.name]: '' }));
  const handleBlur = (e: React.FocusEvent<HTMLInputElement>) => !e.target.value && setFormData(prevState => ({ ...prevState, [e.target.name]: 0 }));

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSubmit(formData);
  };

  console.log(formData)

  return (
    <MDBValidation onSubmit={handleSubmit}>
      <MDBRow className='mb-2'>
        <MDBCol>
          <MDBValidationItem feedback='Lütfen borç adını girin' invalid className='form-item'>
            <span>Borç Adı</span>
            <MDBInput wrapperClass='mb-4' required name='debtName' type='text' value={formData.debtName} onChange={handleChange} />
          </MDBValidationItem>
        </MDBCol>
        <MDBCol>
          <MDBValidationItem feedback='Lütfen alacaklı adını girin' invalid className='form-item'>
            <span>Alacaklı</span>
            <MDBInput wrapperClass='mb-4' required name='lenderName' type='text' value={formData.lenderName} onChange={handleChange} />
          </MDBValidationItem>
        </MDBCol>
      </MDBRow>

      <MDBRow className='mb-2'>
        <MDBCol>
          <MDBValidationItem feedback='Lütfen tutarı girin' invalid className='form-item'>
            <span>Tutar</span>
            <MDBInput
              wrapperClass='mb-4'
              required
              name='debtAmount'
              type='number'
              value={formData.debtAmount}
              onChange={handleChange}
              onFocus={handleFocus}
              onBlur={handleBlur}
            />
          </MDBValidationItem>
        </MDBCol>
        <MDBCol>
          <MDBValidationItem feedback='Lütfen faiz oranını girin' invalid className='form-item'>
            <span>Faiz Oranı (%)</span>
            <MDBInput
              wrapperClass='mb-4'
              required
              name='interestRate'
              type='number'
              value={formData.interestRate}
              onFocus={handleFocus}
              onBlur={handleBlur}
              onChange={handleChange}
            />
          </MDBValidationItem>
        </MDBCol>
      </MDBRow>

      <MDBRow className='mb-2'>
        <MDBCol>
          <MDBValidationItem feedback='' invalid className='form-item'>
            <span>Toplam</span>
            <MDBInput
              wrapperClass='mb-4'
              required
              name='amount'
              type='number'
              value={formData.amount}
              disabled
            />
          </MDBValidationItem>
        </MDBCol>
        <MDBCol>
          <MDBValidationItem feedback='Lütfen başlangıç tarihini girin' invalid className='form-item'>
            <span>Başlangıç Tarihi</span>
            <MDBInput
              wrapperClass='mb-4'
              required
              name='paymentStart'
              type='date'
              value={formData.paymentStart}
              onChange={handleChange}
            />
          </MDBValidationItem>
        </MDBCol>
      </MDBRow>

      <MDBRow className='mb-2'>
        <MDBCol>
          <MDBValidationItem feedback='Lütfen taksit sayısını girin' invalid className='form-item'>
            <span>Taksit Sayısı</span>
            <MDBInput
              wrapperClass='mb-4'
              required
              name='installment'
              type='number'
              value={formData.installment}
              onChange={handleChange}
              onFocus={handleFocus}
              onBlur={handleBlur}
            />
          </MDBValidationItem>
        </MDBCol>
        <MDBCol>
          <MDBValidationItem feedback='Lütfen açıklama girin' invalid className='form-item'>
            <span>Açıklama</span>
            <MDBInput wrapperClass='mb-4' required name='description' type='text' value={formData.description} onChange={handleChange} />
          </MDBValidationItem>
        </MDBCol>
      </MDBRow>

      {error && <div className='error-message'>{error}</div>}

      <MDBBtn type="submit" className="mb-4 w-100">Ödeme Planı Oluştur</MDBBtn>
    </MDBValidation>
  );
};

export default DebtForm;
