import { useEffect } from "react";
import axiosInstance from "../utils/axios";
import { useState } from "react";
import {
  MDBTabsContent,
  MDBBtn,
  MDBInput,
  MDBValidation,
  MDBValidationItem,
  MDBRow,
  MDBCol
} from 'mdb-react-ui-kit';
import Header from '../components/header';

function AddDebts() {

  type PaymentPlan = {
    paymentDate: string;
    paymentAmount: number;
  };

  type FormData = {
    debtName: string;
    lenderName: string;
    debtAmount: string;
    interestRate: number;
    amount: string;
    paymentStart: string;
    installment: number;
    description: string;
    paymentPlan: PaymentPlan[];
  };

  const [error, setError] = useState('');
  const [formData, setFormData] = useState<FormData>({
    debtName: '',
    lenderName: '',
    debtAmount: '0',
    interestRate: 0,
    amount: '0',
    paymentStart: new Date().toISOString().split('T')[0],
    installment: 0,
    description: '',
    paymentPlan: [
      {
        paymentDate: new Date().toISOString().split('T')[0],
        paymentAmount: 0,
      }
    ]
  });

  const formatCurrency = (value: string) => {
    return parseFloat(value).replace(/\./g, 'x').replace(/,/g, '.').replace(/x/g, ',');

  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;

    setFormData(prevState => {
      const updatedData = {
        ...prevState,
        [name]: name === 'paymentStart' ? value : value
      };

      if (name === 'debtAmount' || name === 'interestRate') {
        updatedData.amount = calculateTotalAmount(updatedData.debtAmount, updatedData.interestRate).toString();
        if (name === 'debtAmount') {
          updatedData.debtAmount = formatCurrency(value);
        }
      }

      return updatedData;
    });
  };

  const handleFocus = (e: React.FocusEvent<HTMLInputElement>) => {
    const { name, value } = e.target;

    if (value === '0' || value === '0.00') {
      setFormData(prevState => ({
        ...prevState,
        [name]: ''
      }));
    }
  };

  const handleBlur = (e: React.FocusEvent<HTMLInputElement>) => {
    const { name, value } = e.target;

    if (value === '') {
      setFormData(prevState => ({
        ...prevState,
        [name]: '0'
      }));
    }
  };

  const calculateTotalAmount = (debtAmount: string, interestRate: number) => {


const formattedNum = parseFloat(debtAmount.replace(/\./g, 'x').replace(/,/g, '.').replace(/x/g, ','));
    const debt = parseFloat(debtAmount.replace(/[^0-9.-]+/g, ""));
    return formattedNum + (formattedNum * (interestRate / 100));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    const dataToSend = {
      ...formData,
      debtAmount: parseFloat(formData.debtAmount.replace(/[^0-9.-]+/g, "")),
      interestRate: formData.interestRate ?? 0,
      amount: parseFloat(formData.amount.replace(/[^0-9.-]+/g, "")),
      installment: formData.installment ?? 0
    };

    try {
      const response = await axiosInstance.post('/finance/debt', dataToSend);
      if (response.data.status === 'success') {
        // Başarılı olma durumunu ele al
      } else if (response.data.status === 'error') {
        // Hata durumunu ele al
      }
    } catch (error) {
      setError('Formu gönderirken bir hata oluştu.');
    }
  };

  return (
    <div className="bg-light pages">
      <Header />
      <div>Borç Ekle</div>
      {error && <div className='error-message'>{error}</div>}
      <div className="containerForm">
        <MDBTabsContent>
          <MDBValidation onSubmit={handleSubmit}>
            <MDBRow className='mb-2'>
              <MDBCol>
                <MDBValidationItem feedback='Lütfen Borç Adını girin' invalid className='form-item'>
                  <span>Borç Adı</span>
                  <MDBInput wrapperClass='mb-4' required name='debtName' type='text' value={formData.debtName} onChange={handleChange} />
                </MDBValidationItem>
              </MDBCol>
              <MDBCol>
                <MDBValidationItem feedback='Lütfen Alacaklı Adını girin' invalid className='form-item'>
                  <span>Alacaklı Adı</span>
                  <MDBInput wrapperClass='mb-4' required name='lenderName' type='text' value={formData.lenderName} onChange={handleChange} />
                </MDBValidationItem>
              </MDBCol>
            </MDBRow>

            <MDBRow className='mb-2'>
              <MDBCol>
                <MDBValidationItem feedback='Lütfen Borç Miktarını girin' invalid className='form-item'>
                  <span>Borç Miktarı</span>
                  <MDBInput
                    wrapperClass='mb-4'
                    required
                    name='debtAmount'
                    type='text'
                    value={formData.debtAmount}
                    onChange={handleChange}
                    onFocus={handleFocus}
                    onBlur={handleBlur}
                  />
                </MDBValidationItem>
              </MDBCol>
              <MDBCol>
                <MDBValidationItem feedback='Lütfen Faiz Oranını girin' invalid className='form-item'>
                  <span>Faiz Oranı (%)</span>
                  <MDBInput
                    wrapperClass='mb-4'
                    required
                    name='interestRate'
                    type='number'
                    value={formData.interestRate ?? ''}
                    onFocus={handleFocus}
                    onChange={handleChange}
                  />
                </MDBValidationItem>
              </MDBCol>
            </MDBRow>

            <MDBRow className='mb-2'>
              <MDBCol>
                <MDBValidationItem feedback='Lütfen Toplam Tutarı girin' invalid className='form-item'>
                  <span>Toplam Tutar</span>
                  <MDBInput
                    wrapperClass='mb-4'
                    required
                    name='amount'
                    type='number'
                    value={formData.amount}

                    disabled
                  />
                </MDBValidationItem>
              </MDBCol>
              <MDBCol>
                <MDBValidationItem feedback='Lütfen Ödeme Başlangıç Tarihini girin' invalid className='form-item'>
                  <span>Ödeme Başlangıç Tarihi</span>
                  <MDBInput
                    wrapperClass='mb-4'
                    required
                    name='paymentStart'
                    type='date'
                    value={formData.paymentStart}
                    onChange={handleChange}
                  />
                </MDBValidationItem>
              </MDBCol>
            </MDBRow>

            <MDBRow className='mb-2'>
              <MDBCol>
                <MDBValidationItem feedback='Lütfen Taksit Tutarını girin' invalid className='form-item'>
                  <span>Taksit Tutarı</span>
                  <MDBInput
                    wrapperClass='mb-4'
                    required
                    name='installment'
                    type='number'
                    value={formData.installment ?? ''}
                    onChange={handleChange}
                    onFocus={handleFocus}
                    onBlur={handleBlur}
                  />
                </MDBValidationItem>
              </MDBCol>
              <MDBCol>
                <MDBValidationItem feedback='Lütfen Açıklama girin' invalid className='form-item'>
                  <span>Açıklama</span>
                  <MDBInput wrapperClass='mb-4' required name='description' type='text' value={formData.description} onChange={handleChange} />
                </MDBValidationItem>
              </MDBCol>
            </MDBRow>

            <MDBBtn type="submit" className="mb-4 w-100">Ödeme Planı Oluştur</MDBBtn>
          </MDBValidation>
        </MDBTabsContent>
      </div>
    </div>
  );
}

export default AddDebts;
