import { createSlice, PayloadAction } from '@reduxjs/toolkit';


interface PaymentPlanData {
    id: string;
    paymentAmount: number;
    paymentDate: string;
    isPaid: boolean;
  }

  interface ListState {
    paymentPlan: PaymentPlanData;
  }

const initialState: ListState = {
  paymentPlan: {
    id: '',
    paymentAmount: 0,
    paymentDate: '',
    isPaid: false,
  },
};

const debtSlice = createSlice({
  name: 'paymentPlanList',
  initialState,
  reducers: {
    setPaymentPlanData(state, action: PayloadAction<PaymentPlanData>) {
      state.paymentPlan = action.payload;
    },
  },
});

export const { setPaymentPlanData } = debtSlice.actions;

export default debtSlice.reducer;
