import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
import axiosInstance from "../utils/axios";
import {
  MDBTabsContent,
  MDBBtn,
  MDBInput,
  MDBValidation,
  MDBValidationItem,
  MDBRow,
  MDBCol
} from 'mdb-react-ui-kit';
import Header from '../components/header';

interface PaymentPlanData {
  id: string;
  paymentAmount: number;
  paymentDate: string;
  // Define other fields as needed
}

function PaymentPlanPage() {
  const { id } = useParams<{ id: string }>(); 
  const [error, setError] = useState<string>('');
  const [loading, setLoading] = useState<boolean>(true);
  const [paymentPlans, setPaymentPlans] = useState<PaymentPlanData[]>([]);

  useEffect(() => {
    const fetchPaymentPlans = async () => {
      try {
        const response = await axiosInstance.get<{ status: string; data: PaymentPlanData[] }>(`/finance/payment-plans/${id}`);
        if (response.data.status === 'success') {
          setPaymentPlans(response.data.data);
        } else {
          setError('Veriler alınırken bir hata oluştu.');
        }
      } catch (error) {
        setError('Veriler alınırken bir hata oluştu.');
      } finally {
        setLoading(false);
      }
    };

    fetchPaymentPlans();
  }, [debtId]); // Trigger fetch when debtId changes

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div className='error-message'>{error}</div>;
  }

  return (
    <div className="bg-light pages">
      <Header />
      <div>Payment Plans for Debt ID: {debtId}</div>
      {paymentPlans.length === 0 ? (
        <div>No payment plans found.</div>
      ) : (
        <MDBTabsContent>
          {paymentPlans.map((plan: PaymentPlanData) => (
            <div key={plan.id}>
              <p>Payment Amount: {plan.paymentAmount}</p>
              <p>Payment Date: {new Date(plan.paymentDate).toLocaleDateString()}</p>
              {/* Render other payment plan details here */}
            </div>
          ))}
        </MDBTabsContent>
      )}
    </div>
  );
}

export default PaymentPlanPage;
