import { useEffect } from "react";
import axiosInstance from "../utils/axios";
import { useState } from "react";
import {
  MDBTabsContent,
  MDBBtn,
  MDBInput,
  MDBValidation,
  MDBValidationItem,
  MDBRow,
  MDBCol
} from 'mdb-react-ui-kit';
import Header from '../components/header';

function AddDebts() {

  type PaymentPlan = {
    paymentDate: string;
    paymentAmount: number;
  };

  type FormData = {
    debtName: string;
    lenderName: string;
    debtAmount: string;
    interestRate: number;
    amount: string;
    paymentStart: string;
    installment: number;
    description: string;
    paymentPlan: PaymentPlan[];
  };

  const [error, setError] = useState('');
  const [formData, setFormData] = useState<FormData>({
    debtName: '',
    lenderName: '',
    debtAmount: '0',
    interestRate: 0,
    amount: '0',
    paymentStart: new Date().toISOString().split('T')[0],
    installment: 0,
    description: '',
    paymentPlan: [
      {
        paymentDate: new Date().toISOString().split('T')[0],
        paymentAmount: 0,
      }
    ]
  });

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;

    setFormData(prevState => {
      const updatedData = {
        ...prevState,
        [name]: name === 'paymentStart' ? value : value
      };

      if (name === 'debtAmount' || name === 'interestRate') {
        updatedData.amount = calculateTotalAmount(updatedData.debtAmount, updatedData.interestRate).toString();
        updatedData.paymentPlan = generatePaymentPlan(updatedData.debtAmount, updatedData.installment, updatedData.paymentStart);
      }

      return updatedData;
    });
  };

  const handleFocus = (e: React.FocusEvent<HTMLInputElement>) => {
    const { name, value } = e.target;

    if (value === '0' || value === '0.00') {
      setFormData(prevState => ({
        ...prevState,
        [name]: ''
      }));
    }
  };

  const handleBlur = (e: React.FocusEvent<HTMLInputElement>) => {
    const { name, value } = e.target;

    if (value === '') {
      setFormData(prevState => ({
        ...prevState,
        [name]: '0'
      }));
    }
  };

  const calculateTotalAmount = (debtAmount: string, interestRate: number) => {
    const debt = parseFloat(debtAmount.replace(/[^0-9.-]+/g, ""));
    return debt + (debt * (interestRate / 100));
  };

  const generatePaymentPlan = (debtAmount: string, installment: number, paymentStart: string): PaymentPlan[] => {
    const totalAmount = parseFloat(debtAmount.replace(/[^0-9.-]+/g, ""));
    const paymentAmount = totalAmount / installment;
    const paymentPlans: PaymentPlan[] = [];

    let currentDate = new Date(paymentStart);

    for (let i = 0; i < installment; i++) {
      const paymentDate = currentDate.toISOString().split('T')[0];
      paymentPlans.push({
        paymentDate,
        paymentAmount
      });

      currentDate.setMonth(currentDate.getMonth() + 1); // Move to next month
    }

    return paymentPlans;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    const dataToSend = {
      ...formData,
      debtAmount: parseFloat(formData.debtAmount.replace(/[^0-9.-]+/g, "")),
      interestRate: formData.interestRate ?? 0,
      amount: parseFloat(formData.amount.replace(/[^0-9.-]+/g, "")),
      installment: formData.installment ?? 0
    };

    try {
      const response = await axiosInstance.post('/finance/debt', dataToSend);
      if (response.data.status === 'success') {
        // Handle success case
      } else if (response.data.status === 'error') {
        // Handle error case
      }
    } catch (error) {
      setError('An error occurred while submitting the form.');
    }
  };

  return (
    <div className="bg-light pages">
      <Header />
      <div>Borç Ekle</div>
      {error && <div className='error-message'>{error}</div>}
      <div className="containerForm">
        <MDBTabsContent>
          <MDBValidation onSubmit={handleSubmit}>
            {/* Your form inputs */}
          </MDBValidation>
        </MDBTabsContent>
      </div>
    </div>
  );
}

export default AddDebts;
