import { useEffect } from "react";
import axiosInstance from "../utils/axios";
import { useState } from "react";
import {
  MDBTabsContent,
  MDBBtn,
  MDBInput,
  MDBValidation,
  MDBValidationItem,
}
  from 'mdb-react-ui-kit';

function DebtsPage() {

  interface FormData {
    debtName: string;
    lender: string;
    amount: number;
    interestRate: number;
    total: number;
    startingDate: Date;
    installment: number;
    explanation: string;
  }

  const [error, setError] = useState('')
  const [formData, setFormData] = useState<FormData>({
    debtName: '',
    lender: '',
    amount: 0,
    interestRate: 0,
    total: 0,
    startingDate: new Date(),
    installment: 0,
    explanation: '',
  })

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = async () => {



    const response = await axiosInstance.get('/finance/debt');
    if (response.data.status === 'success') {



    } else if (response.data.status === 'error') {

    }

  }

  return (

    <>
      <div>Debt List</div>
      {error && <div className='error-message'>{error}</div>}
      <MDBTabsContent >
        <MDBValidation onSubmit={handleSubmit} className='px-4'>
          <MDBValidationItem feedback='Please enter name surname' invalid className='form-item'>
            <MDBInput wrapperClass='mb-4' required label='Name Surname' name='debtName' type='text' value={formData.debtName} onChange={handleChange} />
          </MDBValidationItem>
          <MDBValidationItem feedback='Please enter name surname' invalid className='form-item'>
            <MDBInput wrapperClass='mb-4' required label='Name Surname' name='lender' type='text' value={formData.lender} onChange={handleChange} />
          </MDBValidationItem>
          <MDBValidationItem feedback='Please enter name surname' invalid className='form-item'>
            <MDBInput wrapperClass='mb-4' required label='Name Surname' name='amount' type='number' value={formData.amount} onChange={handleChange} />
          </MDBValidationItem>
          <MDBValidationItem feedback='Please enter name surname' invalid className='form-item'>
            <MDBInput wrapperClass='mb-4' required label='Name Surname' name='interestRate' type='number' value={formData.interestRate} onChange={handleChange} />
          </MDBValidationItem>
          <MDBValidationItem feedback='Please enter name surname' invalid className='form-item'>
            <MDBInput wrapperClass='mb-4' required label='Name Surname' name='total' type='number' value={formData.total} onChange={handleChange} />
          </MDBValidationItem>
          <MDBValidationItem feedback='Please enter name surname' invalid className='form-item'>
            <MDBInput wrapperClass='mb-4' required label='Name Surname' name='startingDate' type='date' value={formData.startingDate} onChange={handleChange} />
          </MDBValidationItem>
          <MDBValidationItem feedback='Please enter name surname' invalid className='form-item'>
            <MDBInput wrapperClass='mb-4' required label='Name Surname' name='installment' type='number' value={formData.installment} onChange={handleChange} />
          </MDBValidationItem>
          <MDBValidationItem feedback='Please enter name surname' invalid className='form-item'>
            <MDBInput wrapperClass='mb-4' required label='Name Surname' name='installment' type='number' value={formData.installment} onChange={handleChange} />
          </MDBValidationItem>
          <MDBBtn type="submit" className="mb-4 w-100">Create Payment Plan</MDBBtn>
        </MDBValidation>
      </MDBTabsContent>
    </>
  );
}

export default DebtsPage;
