import { createSlice, PayloadAction } from '@reduxjs/toolkit';

export interface PaymentPlanData {
  id: string;
  paymentAmount: number;
  paymentDate: string;
  isPaid: boolean;
}

interface ListState {
  paymentPlans: PaymentPlanData[];
  error: string;
}

const initialState: ListState = {
  paymentPlans: [],
  error: '',
};

const paymentPlanSlice = createSlice({
  name: 'paymentPlan',
  initialState,
  reducers: {
    setPaymentPlans(state, action: PayloadAction<PaymentPlanData[]>) {
      state.paymentPlans = action.payload;
      state.error = '';
    },
    updatePaymentPlan(state, action: PayloadAction<PaymentPlanData>) {
      const index = state.paymentPlans.findIndex(plan => plan.id === action.payload.id);
      if (index !== -1) {
        state.paymentPlans[index] = action.payload;
      }
    },
  },
});

export const { setPaymentPlans, updatePaymentPlan } = paymentPlanSlice.actions;

export default paymentPlanSlice.reducer;
