import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { RootState } from '../redux/store';
import LoginPage from '../pages/login_register';


const AppRoutes: React.FC = () => {
  const isAuthenticated = useSelector((state: RootState) => state.login.isAuthenticated);

  return (
    <Router>
      <Routes>
        {/* Giriş yapılmamışsa giriş sayfasına yönlendirin */}
        {!isAuthenticated && <Route path="/login" element={<LoginPage />} />}
        {/* Giriş yapılmışsa ana sayfaya yönlendirin 
        {isAuthenticated && <Route path="/" element={<HomePage />} />}
        {/* Giriş yapılmışsa profil sayfasına yönlendirin 
        {isAuthenticated && <Route path="/profile" element={<ProfilePage />} />}
        {/* Giriş yapılmamışsa /login sayfasına yönlendirin 
        {!isAuthenticated && <Route path="*" element={<Navigate to="/login" />} />}*/}
        {/* Giriş yapılmışsa / sayfasına yönlendirin 
        {isAuthenticated && <Route path="*" element={<Navigate to="/dashboard" />} />}*/}
      </Routes>
    </Router>
  );
};

export default AppRoutes;
