import {
    MDBTabsContent,
    MDBBtn,
    MDBInput,
    MDBValidation,
    MDBValidationItem,
}
    from 'mdb-react-ui-kit';
import axios from 'axios';
import { useState } from 'react';

function Register() {

    interface FormData {
        name: string;
        email: string;
        password: string;
        passwordAgain: string;
    }

    const [formData, setFormData] = useState<FormData>({
        name: '',
        email: '',
        password: '',
        passwordAgain: '',
    })
    const [error, setError] = useState('')

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };



    const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();

        if (!(formData.password === formData.passwordAgain)) {
            return setError('passwords do not match')
        } else {
            try {
                const response = await axios.post('https://study.logiper.com/auth/register', formData);

                console.log('Response from server:', response.data);
            } catch (error) {
                console.error('Error:', error);
            }
        }


    };

    return (

        <>
            {error && <div>{error}</div>}
            <form onSubmit={handleSubmit}>
                <MDBTabsContent>
                    <MDBValidation className='row g-3'>
                        <MDBValidationItem className='col-md-4'>
                            <MDBInput wrapperClass='mb-4' required id='validationCustom01' label='Name Surname' name='name' type='text' value={formData.name} onChange={handleChange} />
                        </MDBValidationItem>
                        <MDBValidationItem feedback='Please choose a username.' invalid className='col-md-4'>
                            <MDBInput wrapperClass='mb-4' required label='Email' name='email' type='email' value={formData.email} onChange={handleChange} />
                        </MDBValidationItem>
                        <MDBValidationItem feedback='Please choose a username.' invalid className='col-md-4'>
                            <MDBInput wrapperClass='mb-4' required label='Password' name='password' type='password' value={formData.password} onChange={handleChange} />
                        </MDBValidationItem>
                        <MDBValidationItem feedback='Please choose a username.' invalid className='col-md-4'>
                            <MDBInput wrapperClass='mb-4' required label='Password Again' name='passwordAgain' type='password' />
                        </MDBValidationItem>
                        <MDBBtn type="submit" className="mb-4 w-100">Sign up</MDBBtn>
                    </MDBValidation>
                </MDBTabsContent>

            </form>
        </>

    );
}

export default Register;
