import { createSlice, PayloadAction } from '@reduxjs/toolkit';

export interface PaymentPlanData {
  id: string;
  paymentAmount: number;
  paymentDate: string;
  isPaid: boolean;
}

interface PaymentPlanState {
  paymentPlans: PaymentPlanData[];
  error: string | null;
}

const initialState: PaymentPlanState = {
  paymentPlans: [],
  error: null,
};

const paymentPlansSlice = createSlice({
  name: 'paymentPlans',
  initialState,
  reducers: {
    setPaymentPlans(state, action: PayloadAction<PaymentPlanData[]>) {
      state.paymentPlans = action.payload;
      state.error = null;
    },
    setError(state, action: PayloadAction<string>) {
      state.error = action.payload;
    },
  },
});

export const { setPaymentPlans, setError } = paymentPlansSlice.actions;

export default paymentPlansSlice.reducer;
