import Header from '../components/header';
import { MDBBadge, MDBBtn, MDBTable, MDBTableHead, MDBTableBody } from 'mdb-react-ui-kit';
import axiosInstance from "../utils/axios";
import { useEffect, useState } from 'react';

interface DebtData {
  id: string;
  debtName: string;
  lenderName: string;
  debtAmount: number;
  interestRate: number;
  installment: number;
  paymentStart: string;
  description: string;
}

interface ResponseData {
  status: string;
  data: DebtData[];
}

function DebtsPage() {


  const [error, setError] = useState<string>('');
  const [debtData, setDebtData] = useState<DebtData[]>([]);


  const fetchData = async () => {
    try {
      const response = await axiosInstance.get<ResponseData>('/finance/debt');
      if (response.data.status === 'success') {
        const debtData = response.data.data;
        setDebtData(debtData)


      } else if (response.data.status === 'error') {
        setError('Veriler alınırken bir hata oluştu.');
      }
    } catch (error) {
      setError('Formu gönderirken bir hata oluştu.');
    }
  };

  useEffect(() => {
    fetchData()
  }, [])

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;

    setFormData(prevState => {
      const updatedData = {
        ...prevState,
        [name]: name === 'paymentStart' ? value : value
      };


      if (name === 'debtAmount' || name === 'interestRate' || name === 'installment') {
        const numericValue = parseFloat(value);
        if (!isNaN(numericValue)) {
          updatedData[name] = numericValue;
          if (name === 'debtAmount' || name === 'interestRate') {
            updatedData.amount = calculateTotalAmount(updatedData.debtAmount, updatedData.interestRate);
          }
          if (name === 'debtAmount' || name === 'installment') {
            updatedData.paymentPlan = generatePaymentPlan(formData.amount, formData.installment, formData.paymentStart);
          }
        }
      }


      return updatedData;

    });
  };




  return (
    <div className="bg-light pages">
      <Header />
      <div>Debt List</div>
      {error && <div className='error-message'>{error}</div>}
      <MDBTable align='middle'>
        <MDBTableHead>

          <tr>
            <th scope='col'>Debt Name</th>
            <th scope='col'>Lender Name</th>
            <th scope='col'>Debt Amount</th>
            <th scope='col'>Interest Rate</th>
            <th scope='col'>Installment</th>
            <th scope='col'>Total Amount</th>
            <th scope='col'>Create Date</th>
            <th scope='col'>Payment Start</th>
            <th scope='col'>Update Date</th>
            <th scope='col'>Description</th>
            <th scope='col'>Actions</th>
            <th></th>
          </tr>
        </MDBTableHead>
        <MDBTableBody>
          {debtData.map((debt: any, index: number) => (
            <tr key={index}>
              <td>{debt.debtName}</td>
              <td>{debt.lenderName}</td>
              <td>{debt.debtAmount}</td>
              <td>%{debt.interestRate}</td>
              <td>{debt.installment}</td>
              <td>{debt.amount}</td>
              <td>{new Date(debt.createdAt).toISOString().split('T')[0]}</td>
              <td>{new Date(debt.paymentStart).toISOString().split('T')[0]}</td>
              <td>{new Date(debt.updatedAt).toISOString().split('T')[0]}</td>
              <td>{debt.description}</td>
              <td>
                <MDBBtn className='text-nowrap' color='warning' size='sm'>
                  Edit
                </MDBBtn>
              </td>
              <td>
                <MDBBtn className=' text-nowrap' color='info' size='sm'>
                  See Payment Plan
                </MDBBtn>
              </td>
              <td>
              <MDBBtn className='mx-2' color='danger' onClick={handleDelete}>
        Delete
      </MDBBtn>
              </td>
            </tr>
          ))}
        </MDBTableBody>
      </MDBTable>
    </div>
  );
}

export default DebtsPage;
