import { combineReducers } from '@reduxjs/toolkit';
import loginReducer from './login_slice'; 
import auth_slice from './auth_slice';

const rootReducer = combineReducers({
  login: loginReducer,
  auth: auth_slice,
});

const persistConfig = {
  key: 'root',
  storage,
  // Diğer yapılandırma seçeneklerini buraya ekleyebilirsiniz
