import {
    MDBTabsContent,
    MDBTabsPane,
    MDBBtn,
    MDBIcon,
    MDBInput,
    MDBCheckbox
}
    from 'mdb-react-ui-kit';
import axios from 'axios';
import { useState } from 'react';

function Register() {

    interface FormData {
        name: string;
        email: string;
        password: string;
        passwordAgain: string;
    }

    const [formData, setFormData] = useState<FormData>({
        name: '',
        email: '',
        password: '',
        passwordAgain: '',
    })
    const [error, setError] = useState('')

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };



    const handleSubmit = async () => {

        if(!(formData.password === formData.passwordAgain)) {
            return setError('passwords do not match')
        }  else

        try {
            const response = await axios.post('https://study.logiper.com/auth/register', formData);

            console.log('Response from server:', response.data);
        } catch (error) {
            console.error('Error:', error);
        }
    };

    return (


        <form onSubmit={handleSubmit}>
            <MDBTabsContent>
                <MDBInput wrapperClass='mb-4' label='Name Surname' name='name' type='text' value={formData.name} onChange={handleChange} />
                <MDBInput wrapperClass='mb-4' label='Email' name='email' type='email' value={formData.email} onChange={handleChange} />
                <MDBInput wrapperClass='mb-4' label='Password' name='password' type='password' value={formData.password} onChange={handleChange} />
                <MDBInput wrapperClass='mb-4' label='Password Again' name='passwordAgain' type='password' />
                <MDBBtn type="submit" className="mb-4 w-100">Sign up</MDBBtn>
            </MDBTabsContent>
        </form>


    );
}

export default Register;
